mapper.update:
UPDATE pagopa_ma_entity

    en_updated_datetime = CURRENT_TIMESTAMP,

        en_name = #{entity.name},


        en_support_email = #{entity.supportEmail}


WHERE en_id = #{entityId}
AND en_is_deleted = 0

UPDATE pagopa_ma_entity
SET
    en_is_deleted = 1,
    en_deleted_datetime = #{entityDeletedDatetime}
WHERE en_id = #{entityId}
AND en_is_deleted = 0
